mapper.update:
update sys_org set

  left_num = left_num + 2 where left_num >= #{rightNum}


  left_num = left_num - #{treeNodeNum} where left_num >= #{rightNum}

update sys_org set

    right_num = right_num + 2 where right_num >= #{rightNum}


    right_num = right_num - #{treeNodeNum} where right_num >= #{rightNum}

update sys_org
parent_id = #{parentId}, left_num = #{leftNum}, right_num = #{rightNum}, code = #{code}, name = #{name}, name_py = #{namePy}, type = #{type}, level = #{level}, state = #{state}, sort = #{sort}, remarks = #{remarks}, del_flag = #{delFlag}
where id = #{id}

UPDATE sys_org
SET del_flag = '1'
WHERE id = #{id}
