mapper.update:
UPDATE sys_user
login_name = #{loginName}, org_id = #{orgId}, name = #{name}, email = #{email}, phone = #{phone}, mobile = #{mobile}, state = #{state}, update_date = #{updateDate}, remarks = #{remarks}
WHERE id = #{id}

UPDATE sys_user
SET password = #{password},
last_password_reset_date = #{lastPasswordResetDate}
WHERE id = #{id}

UPDATE sys_user
SET del_flag = '1', login_name = concat(login_name, '_del')
WHERE id = #{id}
